import {  Button, Grid, Link, Paper, TextField, Typography } from "@mui/material";
import React, { useState } from "react";
// import bcrypt from 'bcrypt';

import AppLogo from './../../images/SignIn&SignUp/lwre.png';
import Logo from './../../images/SignIn&SignUp/sliit_logo.png';

const paperStyle={padding:20, height:'auto', width:300, margin:'20px auto'};
const paperStyle2={padding:10};
const textStyle={margin:'0px 0px 12px 0px'};
const btnStyle={margin:'8px 0'};
const typoStyle={align:'center'};

const SignIn=()=>{

  const [input,setInput] = useState({
    
  });
  
  const handleChange = (e) =>{
    setInput({...input,[e.target.name]:e.target.value});
  }

  const handleSubmit = (e) =>{

    e.preventDefault();

    const email=input.email.current.value;
    const password=input.password.current.value;

    console.log(input);
    console.log("input: " + email +" "+ password);
  }

  return(
    <Grid>
      <Paper elevation={10} style={paperStyle2}>
        <div align="left">
        <img src={AppLogo} alt="Logo" />
        </div>
        </Paper>
      <Paper elevation={10} style={paperStyle}>
        <Grid align='center'>
          <img src={Logo} alt="Logo" />
          <h2>Sign In </h2>
        </Grid>
        <form onSubmit={handleSubmit}>
        <TextField label="Enter SLIIT Email Address" type="email" name="email" fullWidth='true' required style={textStyle} value={input.email} onChange={handleChange} />
        <TextField label="Password"  type="password" name="password" fullWidth='true' required style={textStyle} value={input.password} onChange={handleChange}/>
        <Button type="submit" color="primary" variant="contained" fullWidth style={btnStyle}>Sign In</Button>
        <div class="g-signin2" data-onsuccess="onSignIn"></div>
        </form>
        <div align='center'>
        <Typography class={typoStyle}>
          <Link href="#">
          Forgot Password?
          </Link>
        </Typography>
        </div>
        <div align='center'>
        <Typography>Don't you have an account?
          <Link href="signup">
            Sign Up
          </Link>
        </Typography>
        </div>
      </Paper>
    </Grid>
  );
}


export default SignIn;
