import {  Button, Grid, Link, Paper, TextField, Typography } from "@mui/material";
import React, { useState } from "react";
import Logo from './../../images/SignIn&SignUp/lwre.png';

const paperStyle={padding:20, height:'70vh', width:300, margin:'20px auto'};
const paperStyle2={padding:20};
const textStyle={margin:'0px 0px 12px 0px'};
const btnStyle={margin:'8px 0'};
const typoStyle={align:'center'};

const SignIn=()=>{

  const [input,setInput] = useState({
    
  });
  
  const handleChange = (e) =>{
    setInput({...input,[e.target.name]:e.target.value});
  }
  
  const handleSubmit = (e) =>{
    e.preventDefault();
    console.log(input);
  }

  return(
    <Grid>
      <Paper elevation={10} style={paperStyle2}>
        <div align="center">ProjectName
        </div>
        </Paper>
      <Paper elevation={10} style={paperStyle}>
        <Grid align='center'>
          <img src={Logo} alt="Logo" />
          <h2>Sign In to ProjectProof</h2>
        </Grid>
        <form onSubmit={handleSubmit}>
        <TextField label="Username" placeholder="Enter Username" type="text" name="username" fullWidth='true' required style={textStyle} value={input.username} onChange={handleChange} />
        <TextField label="Password" placeholder="Enter Password" type="password" name="password" fullWidth='true' required style={textStyle} value={input.password} onChange={handleChange}/>
        <Button type="submit" color="primary" variant="contained" fullWidth style={btnStyle}>Sign In</Button>
        </form>
        <div align='center'>
        <Typography class={typoStyle}>
          <Link href="#">
          Forgot Password?
          </Link>
        </Typography>
        </div>
        <div align='center'>
        <Typography>Do you have an account?
          <Link href="#">
            Sign Up
          </Link>
        </Typography>
        </div>
      </Paper>
    </Grid>
  );
}


export default SignIn;
