import {  Button, Grid, Link, Paper, TextField, Typography } from "@mui/material";
import React, { useState } from "react";
import Logo from './../../images/SignIn&SignUp/sliit_logo.png';

const paperStyle={padding:20, height:'70vh', width:300, margin:'20px auto'};
const textStyle={margin:'0px 0px 12px 0px'};
const btnStyle={margin:'8px 0'};
const typoStyle={align:'center'};

const [input,setInput] = useState({
  email: "",
  pawssword: ""
});

const handleChange = (e) =>{
  setInput({...input,[e.target.name]:e.target.value});
}

const handleSubmit = (e) =>{
  e.preventDefault();
  console.log(input)
}



const SignIn=()=>{
  return(
    <Grid>
      <Paper elevation={10} style={paperStyle}>
        <Grid align='center'>
          <img src={Logo} alt="Logo" />
          <h2>Sign In to ProjectProof</h2>
        </Grid>
        <form onSubmit={handleSubmit}>
        <TextField label="Username" placeholder="Enter Username" fullWidth='true' required style={textStyle} value={input.email} onChange={handleChange} />
        <TextField label="Password" placeholder="Enter Password" type="password" fullWidth='true' required style={textStyle} value={input.password} onChange={handleChange}/>
        <Button type="submit" color="primary" variant="contained" fullWidth style={btnStyle}>Sign In</Button>
        </form>
        <div align='center'>
        <Typography class={typoStyle}>
          <Link href="#">
          Forgot Password?
          </Link>
        </Typography>
        </div>
        <div align='center'>
        <Typography>Do you have an account?
          <Link href="#">
            Sign Up
          </Link>
        </Typography>
        </div>
      </Paper>
    </Grid>
  );
}


export default SignIn;
