import react, {useState} from 'react';
import {useHistory} from 'react-router-dom';

const SignIn = (props) =>{
  const history = useHistory();
  const [credentials,setCredentials] = useState({
    email:'',
    password:''});

    return (
      <div>
        <input value={credentials.email} type="text" placeholder="Enter Email Address"
        onChange={e=> setCredentials({email:e.target.value, password:credentials.password})} />

        <input value={credentials.password} type="password" placeholder="Enter Password"
        onChange={e=> setCredentials({email:credentials.email, password:e.target.value})} />

        <button
            disabled={ !(/^([A-Za-z0-9_\-.])+@([A-Za-z0-9_\-.])+\.([A-Za-z]{2,})$/.test(credentials.email)) }
            onClick={()=>{
              if (credentials.password === "let-me-in")
                history.push("/secret");
            }}>Login</button>
      </div>
    )

}

export default SignIn;
